import { useState } from "react";
import "../styles/product.css";

export default function ProductCard({product}) {
    const [image, setImage] = useState(product.image[0]);
    const [stock, setStock] = useState(product.stock);

    function changeImage(newImage) {
        setImage(newImage);
    }

    function addToCart() {
        if (stock > 0) {
            setStock(stock - 1)
        }
    }

    return (
        <div className="product-card col-md-4">
            <div className="resizer">
                <img src={image} className="big-image" alt="Menu de test"/>
                <div className="three-images mt-2">
                    <img src={product.image[1]} alt="" onClick={() => changeImage(product.image[1])}/>
                    <img src={product.image[2]} alt="" onClick={() => changeImage(product.image[2])}/>
                    <img src={product.image[3]} alt="" onClick={() => changeImage(product.image[3])}/>
                </div>
                <div className="mt-3 text-container">
                    <h4>Price : <strong>{product.price} Ar</strong></h4>
                    <h4>Stock : <strong>{stock}</strong></h4>
                    <button href="#" className="btn btn-primary mt-2" onClick={addToCart}>Add to cart</button>
                </div>
            </div>
        </div>
    );
}
