import { useState } from "react";
import Filter from "./Filter";
import ProductList from "./ProductList";

export default function ProductContainer({ products }) {

    const [minPrice, setMinPrice] = useState("");
    const [maxPrice, setMaxPrice] = useState("");
    const [currentProducts, setCurrentProducts] = useState(products);

    function filterProducts() {
        console.log("Prix minimum : " + minPrice + " et " + " Prix maximum : " + maxPrice);
        if (minPrice == "" || maxPrice == "") {
            setCurrentProducts(products);
            return;
        }

        const resultProducts = [];

        products.forEach((product) => {
            if (product.price < maxPrice && product.price > minPrice) {
                resultProducts.push(product);
            }
        });

        setCurrentProducts(resultProducts);
    }

    return (
        <div className="container">
            <div className="mt-2">
                <h4>All products</h4>
                <div className="product-list row mt-2">
                    <ProductList products={currentProducts} />
                    <Filter
                        products={currentProducts}
                        minPrice={minPrice}
                        maxPrice={maxPrice}
                        onMinPriceChange={setMinPrice}
                        onMaxPriceChange={setMaxPrice}
                        filterHandler={filterProducts} />
                </div>
            </div>
        </div>
    );
}
